        #loginbox {
            margin-top: 3%;
            box-shadow: 10px 10px 10px #444, 15px 15px 15px #555;
            height: 86%;
            background: none !important;
        }

        #loginform {
            background: none !important;
        }

        .main_input_box1 {
            margin-top: 30px
        }

        .main_input_box1,
        .main_input_box2 {
            border: none;
            background: none;


        }

        .main_input_box1 span,
        .main_input_box2 span {
            background: none;
            margin-right: 4%;
            color: silver;
        }

        .main_input_box1 i,
        .main_input_box2 i {
            background: none;
            font-size: 30px;
            padding: 0;
        }

        .main_input_box1 .login,
        .main_input_box2 .pass {
            border: none;
            font-size: 15px;
            background: none;
            border-bottom: 2px solid greenyellow;
            border-radius: 20%;
            width: 80%;
            color: #FFF;
            text-align: center
        }

        .main_input_box1 .login:focus,
        .main_input_box2 .pass:focus {
            border: none;
            background: none;
            border-bottom: 5px solid green;
            border-radius: 20%;
            box-shadow: none;
        }


